package org.primaldev.ppm.ui.admin;



public class GlobalSettings {
	
	private String reportCatagory = "Activiti-Report";
	private String adminCatagory  = "Admin-Process";
	
	public GlobalSettings() {
		// TODO Auto-generated constructor stub
	}

	public String getReportCatagory() {
		return reportCatagory;
	}

	public void setReportCatagory(String reportCatagory) {
		this.reportCatagory = reportCatagory;
	}

	public String getAdminCatagory() {
		return adminCatagory;
	}

	public void setAdminCatagory(String adminCatagory) {
		this.adminCatagory = adminCatagory;
	}

}
